import React from 'react'
import { useEffect,useState } from 'react';
import axios from "axios";
import {Paper,Zoom,Container,Box,Grid,Typography,Button} from '@mui/material';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';



const columns = [
    { id: 'srno', label: 'S. No.', minWidth: 50,align: 'center' },
    { id: 'srno', label: 'First Name', minWidth: 50,align: 'center' },
    { id: 'srno', label: 'Last Name', minWidth: 50,align: 'center' },
    { id: 'srno', label: 'Email', minWidth: 50,align: 'center' },
    { id: 'srno', label: 'Action', minWidth: 50,align: 'center' }
   
]



const CustomerList = () => {



    const [customerList, setCustomerList] = useState([]);

    useEffect(()=>{
        const fetchAllUser = async()=>{
            try{
                const res = await axios.get("http://localhost:8800/users");
                setCustomerList(res.data)
                console.log(res);

            }catch(err){
                console.log(err)
            }
        }


        fetchAllUser();
    },[])


  return (
    <>

<Container>
        <Box sx={{ my: 2 }}>
      
      <Paper elevation={3} style={{marginTop:"30px",paddingLeft:"16px"}}>
               
                <Grid xs={12} container direction="row" justifyContent="flex-start" alignItems="flex-start">

                <Grid xs={12} style={{padding:"24px"}}>

                    <Button
                    variant='contained'
                    >
                    Add Customer

                    </Button>
                </Grid>

                <Grid xs={12}>
                    <Typography>
                        Customer List
                    </Typography>


                    {
                        columns.map(customer =>(
                            <TableContainer >
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{minWidth: column.minWidth,background:"rgba(0,0,0,0.2)" }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>


          <TableBody>
                    {customerList.map((column) => (
                  <TableRow hover role="checkbox" tabIndex={-1} style={{padding:"8px"}}>
                        <TableCell>{index + 1}</TableCell>
                        <TableCell >{column.firstName}</TableCell>
                        <TableCell>{column.lastName}</TableCell>
                        <TableCell >{column.email}</TableCell>
                  </TableRow>
                    ))
}
            
          </TableBody>
            </Table>
            </TableContainer>
                        ))
                    }




                </Grid>
                
                </Grid>
        </Paper>
        </Box>
        </Container>
        
    </>
  )
}

export default CustomerList;
